package Educative.Patterns.SlidingWindow;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Given two strings s and p, return an array of all the start indices of p's anagrams in s. You may return the answer in any order.
 *
 * An Anagram is a word or phrase formed by rearranging the letters of a different word or phrase, typically using all the original letters exactly once.
 *
 *
 *
 * Example 1:
 *
 * Input: s = "cbaebabacd", p = "abc"
 * Output: [0,6]
 * Explanation:
 * The substring with start index = 0 is "cba", which is an anagram of "abc".
 * The substring with start index = 6 is "bac", which is an anagram of "abc".
 * Example 2:
 *
 * Input: s = "abab", p = "ab"
 * Output: [0,1,2]
 * Explanation:
 * The substring with start index = 0 is "ab", which is an anagram of "ab".
 * The substring with start index = 1 is "ba", which is an anagram of "ab".
 * The substring with start index = 2 is "ab", which is an anagram of "ab".
 */

public class AnagramsInString {

    public static void main(String[] args) {
        System.out.println(findAnagrams("cbaebabacd", "abc")); // true
        System.out.println(findAnagrams("abab", "ab")); // true
        System.out.println(findAnagrams("ab", "eidbaooo")); // true
        System.out.println(findAnagrams("de","abcdefg")); //true
        System.out.println(findAnagrams("ed","abcdefg")); //true
        System.out.println(findAnagrams("adc", "dcda")); //true
        System.out.println(findAnagrams("hello","ooollehoooleh")); //true
        System.out.println(findAnagrams("hello","ooolehooolleh")); //true
        System.out.println(findAnagrams("helo","ehdoleh")); //true
        System.out.println(findAnagrams("ky", "ainwkckifykxlribaypk")); //true
        System.out.println(findAnagrams("trinitrophenylmethylnitramine"
                ,"dinitrophenylhydrazinetrinitrophenylmethylnitramine")); //true
        System.out.println(findAnagrams("vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
                "")); //false
        System.out.println("-------------------");
        System.out.println(findAnagrams("ab", "eidboaoo")); // false
        System.out.println(findAnagrams("df","abcdefg")); //false
        System.out.println(findAnagrams("hello","ooolleoooleh")); //false
        System.out.println(findAnagrams("zftgfmnoxmfkwufwadd",
                "")); //false
    }

    public static List<Integer> findAnagrams(String s, String p) {

        if(s.length() < p.length()){
            String tmp = s;
            s = p;
            p = tmp;
        }

        if(s == null || p == null || s.length() < p.length())
            return new ArrayList<Integer>();

        List<Integer> result = new ArrayList<>();
        Map<Character, Integer> sMap = new HashMap<>();
        Map<Character, Integer> pMap = new HashMap<>();
        
        for(int i=0; i< p.length(); i++){
            pMap.put(p.charAt(i), pMap.getOrDefault(p.charAt(i), 0) + 1);
            sMap.put(s.charAt(i), sMap.getOrDefault(s.charAt(i), 0) + 1);
        }
        
        if(sMap.equals(pMap))
            result.add(0);

        for(int i=0; i< s.length() - p.length(); i++){
            char key = s.charAt(p.length() + i);
            sMap.put(key, sMap.getOrDefault(key, 0) + 1);
            sMap.put(s.charAt(i), sMap.get(s.charAt(i)) - 1);
            if(sMap.get(s.charAt(i)) == 0)
                sMap.remove(s.charAt(i));

            if(sMap.equals(pMap))
                result.add(i+1);
            
        }
        
        return result;
    }
}
